public struct ThemeShapeStyleGenerator: Sendable {

    public init() {}

    public func generate() -> GeneratedFile {
        let content = """
        // Generated by ThemeKit â€” do not edit

        import SwiftUI
        import ThemeKit

        public struct ThemeShapeStyle<Style: ShapeStyle>: ShapeStyle, Sendable {
            let keyPath: KeyPath<Theme, ThemeAdaptiveStyle<Style>>

            public init(keyPath: KeyPath<Theme, ThemeAdaptiveStyle<Style>>) {
                self.keyPath = keyPath
            }

            public func resolve(in environment: EnvironmentValues) -> some ShapeStyle {
                environment.theme[keyPath: keyPath].resolved(for: environment.colorScheme)
            }
        }

        """
        return GeneratedFile(name: "ThemeShapeStyle.swift", content: content)
    }
}
